import styled from 'styled-components';

export const Container = styled.div`
    width: 100%;
    heigth: 80px;
    padding: 0 16px;
    display; flex;
    align-items: center;
    justify-content: space-between;
`
export const NumberContainer = styled.div`
width: 50px;
heigth: 50px;
display; flex;
align-items: center;
justify-content: center;
flex-direction: column

h1{
    fint-size: 20px;
    text-align: center;
    color: #444;
}

h2{
    fint-size: 12px;
    text-align: center;
    color: #777;
}

&:hover{
    cursor: pointer;
    background: rgba(0, 0, 0, .03);
    transition: .4s ease;
    border-radius: 10px;
}
`
